import React, { useState, useEffect } from "react";
import axios from "axios";
import './Teacher.css';
const Teachers = () => {
  const [teachers, setTeachers] = useState([]);
  const [error, setError] = useState(null);
  const [editTeacher, setEditTeacher] = useState(null); // For editing a teacher

  // Fetch all teachers on component mount
  useEffect(() => {
    fetchTeachers();
  }, []);

  const fetchTeachers = async () => {
    try {
      const response = await axios.get("http://localhost:8080/api/teachers");
      setTeachers(response.data);
      setError(null);
    } catch (err) {
      setError("Failed to load teachers. Please try again later.");
      console.error("Error fetching teachers:", err);
    }
  };

  // Handle delete teacher
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/teachers/${id}`);
      setTeachers(teachers.filter((teacher) => teacher.teacherId !== id));
    } catch (err) {
      setError("Failed to delete teacher. Please try again.");
      console.error("Error deleting teacher:", err);
    }
  };

  // Handle edit teacher
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    try {
      const updatedTeacher = {
        teacherName: editTeacher.teacherName,
        teacherPhone: editTeacher.teacherPhone,
        teacherEmail: editTeacher.teacherEmail,
        subjects: [{ name: editTeacher.subject }],
      };

      await axios.put(
        `http://localhost:8080/api/teachers/updateTeacher/${editTeacher.teacherId}`,
        updatedTeacher
      );

      setTeachers((prevTeachers) =>
        prevTeachers.map((teacher) =>
          teacher.teacherId === editTeacher.teacherId ? updatedTeacher : teacher
        )
      );

      setEditTeacher(null); // Close the edit form
    } catch (err) {
      setError("Failed to update teacher. Please try again.");
      console.error("Error updating teacher:", err);
    }
  };

  return (
    <div className="teachers-container">
      <h1>Teachers List</h1>

      {/* Display List of Teachers */}
      <table className="teachers-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Subjects</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {teachers.map((teacher) => (
            <tr key={teacher.teacherId}>
              <td>{teacher.teacherId}</td>
              <td>{teacher.teacherName}</td>
              <td>{teacher.teacherPhone}</td>
              <td>{teacher.teacherEmail}</td>
              <td>{teacher.subjects.map((sub) => sub.name).join(", ")}</td>
              <td>
                <button
                  className="edit-button"
                  onClick={() => setEditTeacher(teacher)}
                >
                  Edit
                </button>
                <button
                  className="delete-button"
                  onClick={() => handleDelete(teacher.teacherId)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Error Message */}
      {error && <p className="error">{error}</p>}

      {/* Edit Teacher Form */}
      {editTeacher && (
        <div className="edit-teacher-form">
          <h2>Edit Teacher</h2>
          <form onSubmit={handleEditSubmit}>
            <input
              type="text"
              name="teacherName"
              value={editTeacher.teacherName}
              onChange={(e) =>
                setEditTeacher({ ...editTeacher, teacherName: e.target.value })
              }
              placeholder="Teacher Name"
              required
            />
            <input
              type="text"
              name="teacherPhone"
              value={editTeacher.teacherPhone}
              onChange={(e) =>
                setEditTeacher({ ...editTeacher, teacherPhone: e.target.value })
              }
              placeholder="Phone Number"
              required
            />
            <input
              type="email"
              name="teacherEmail"
              value={editTeacher.teacherEmail}
              onChange={(e) =>
                setEditTeacher({ ...editTeacher, teacherEmail: e.target.value })
              }
              placeholder="Email"
              required
            />
            <input
              type="text"
              name="subject"
              value={editTeacher.subject || ""}
              onChange={(e) =>
                setEditTeacher({
                  ...editTeacher,
                  subject: e.target.value,
                })
              }
              placeholder="Subject"
              required
            />
            <button type="submit">Save</button>
            <button
              type="button"
              onClick={() => setEditTeacher(null)} // Cancel edit
            >
              Cancel
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default Teachers;
