import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Teacher.css"; // Import your CSS for styling

const Teachers = () => {
  const [teachers, setTeachers] = useState([]);
  const [error, setError] = useState(null);

  // Fetch teachers list on component mount
  useEffect(() => {
    const fetchTeachers = async () => {
      try {
        const response = await axios.get("http://localhost:8080/api/teachers");
        setTeachers(response.data);
      } catch (err) {
        setError("Failed to load teachers. Please try again later.");
        console.error("Error fetching teachers:", err);
      }
    };

    fetchTeachers();
  }, []);

  // Add a new teacher
  const handleAddTeacher = async (e) => {
    e.preventDefault();
    try {
      const newTeacher = {
        teacherName: e.target.teacherName.value,
        teacherPhone: e.target.teacherPhone.value,
        teacherEmail: e.target.teacherEmail.value,
        subjects: [
          {
            name: e.target.subjectName.value,
          },
        ],
      };

      console.log("Sending data to server:", newTeacher); // Debugging log

      const response = await axios.post("http://localhost:8080/api/teachers/addTeacher", newTeacher);
      console.log("Response from server:", response);

      // Update state with the new teacher
      setTeachers((prev) => [...prev, response.data]);
      e.target.reset();
      setError(null);
    } catch (err) {
      setError("Failed to add teacher. Please try again.");
      console.error("Error adding teacher:", err);
    }
  };

  return (
    <div className="teachers-container">
      <h1>Teachers List</h1>

      {/* Display List of Teachers */}
      <table className="teachers-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Subjects</th>
          </tr>
        </thead>
        <tbody>
          {teachers.map((teacher) => (
            <tr key={teacher.teacherId}>
              <td>{teacher.teacherId}</td>
              <td>{teacher.teacherName}</td>
              <td>{teacher.teacherPhone}</td>
              <td>{teacher.teacherEmail}</td>
              <td>
                {teacher.subjects.map((subject) => (
                  <span key={subject.id}>{subject.name}</span>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Error Message */}
      {error && <p className="error">{error}</p>}

      {/* Add Teacher Form */}
      <div className="add-teacher-form">
        <h2>Add Teacher</h2>
        <form onSubmit={handleAddTeacher}>
          <input type="text" name="teacherName" placeholder="Teacher Name" required />
          <input type="text" name="teacherPhone" placeholder="Phone Number" required />
          <input type="email" name="teacherEmail" placeholder="Email" required />
          <input type="text" name="subjectName" placeholder="Subject" required />
          <button type="submit">Add Teacher</button>
        </form>
      </div>
    </div>
  );
};

export default Teachers;
