import React, { useEffect, useState } from 'react';
import axios from '../axiosInstance';

const Classes = () => {
  const [classes, setClasses] = useState([]);
  const [students, setStudents] = useState([]); // Store students of a class
  const [error, setError] = useState(null);

    const [updateStudent, setUpdateStudent] = useState(null); // State for the student to be updated
    //const [formError, setFormError] = useState(null);

  useEffect(() => {
    const fetchClasses = async () => {
      try {
        const response = await axios.get('/classes/allclasses'); // Replace with your backend endpoint for classes
        setClasses(response.data);
      } catch (err) {
        console.error('Error fetching classes:', err);
        setError('Failed to load classes. Please try again later.');
      }
    };

    fetchClasses();
  }, []);

  const handleViewClass = async (className, section) => {
    try {
      const response = await axios.get(`/students/class/${className}/${section}`); // Call backend endpoint
      setStudents(response.data); // Update students state with the response
    } catch (err) {
      console.error('Error fetching class details:', err);
      setError('Failed to fetch class details. Please try again later.');
    }
  };

  const handleEdit = (student) => {
    setUpdateStudent(student);
  };

  
  const handleUpdateChange = (e) => {
    const { name, value } = e.target;
    setUpdateStudent((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    if (!updateStudent.rollNumber || !updateStudent.name || !updateStudent.age || !updateStudent.studentClassName) {
      setFormError('All fields are required!');
      return;
    }



  const handleDelete = async (id) => {
    try {
      await axiosInstance.delete(`/students/deleteStudent/${id}`);
      setStudents((prevStudents) => prevStudents.filter((student) => student.id !== id));
    } catch (err) {
      console.error('Error deleting student:', err);
      setError('Failed to delete student. Please try again later.');
    }
  };

  try {
    const response = await axiosInstance.put(`/students/updateStudent/${updateStudent.id}`, updateStudent);
    setStudents((prevStudents) => prevStudents.map((student) =>
      student.id === updateStudent.id ? response.data : student
    ));
    setUpdateStudent(null); // Close the form after updating
    setFormError(null);
  } catch (err) {
    console.error('Error updating student:', err);
    setError('Failed to update student. Please try again later.');
  }
};
  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h1>Class List</h1>
      <table border="1" style={{ margin: '0 auto', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Class Name</th>
            <th>Section</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {classes.map((classItem) => (
            <tr key={classItem.id}>
              <td>{classItem.id}</td>
              <td>{classItem.name}</td>
              <td>{classItem.section}</td>
              <td>
                <button onClick={() => handleViewClass(classItem.name, classItem.section)}>View</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {students.length > 0 && (
        <div style={{ marginTop: '20px' }}>
          <h2>Students in Class</h2>
          <table border="1" style={{ margin: '0 auto', marginTop: '10px' }}>
            <thead>
              <tr>
                <th>ID</th>
                <th>Roll Number</th>
                <th>Name</th>
                <th>Age</th>
                <th>Grade</th>
                <th>Class</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student.id}>
                  <td>{student.id}</td>
                  <td>{student.rollNumber}</td>
                  <td>{student.name}</td>
                  <td>{student.age}</td>
                  <td>{student.grade}</td>
                  <td>{student.studentClassName}</td>
                  <td>
                {/* Update button beside each student */}
                <button onClick={() => handleEdit(student)}>Edit</button>
                <button onClick={() => handleDelete(student.id)}>Delete</button>
              </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {error && <div style={{ color: 'red', marginTop: '20px' }}>{error}</div>}
    </div>
  );
};

export default Classes;
