import React, { useEffect, useState } from 'react';
import axios from '../axiosInstance';

const Classes = () => {
  const [classes, setClasses] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClasses = async () => {
      try {
        const response = await axios.get('/classes/allclasses'); // Replace with your backend endpoint
        setClasses(response.data);
      } catch (err) {
        console.error('Error fetching classes:', err);
        setError('Failed to load classes. Please try again later.');
      }
    };

    fetchClasses();
  }, []);

  const handleViewClass = (classItem) => {
    alert(`Viewing details for Class: ${classItem.name}, Section: ${classItem.section}`);
    // Add any additional logic here (e.g., navigate to another page or open a modal)
  };

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h1>Class List</h1>
      <table border="1" style={{ margin: '0 auto', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Class Name</th>
            <th>Section</th>
          </tr>
        </thead>
        <tbody>
          {classes.map((classItem) => (
            <tr key={classItem.id}>
              <td>{classItem.id}</td>
              <td>{classItem.name}</td>
              <td>{classItem.section}</td>
              <td>
                <button onClick={() => handleViewClass(classItem)}>View</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Classes;
