import React, { useEffect, useState } from 'react';
import axios from '../axiosInstance';

const Classes = () => {
  const [classes, setClasses] = useState([]);
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);

  const [newStudent, setNewStudent] = useState({
    rollNumber: '',
    name: '',
    age: '',
    grade: '',
    studentClassName: ''
  });

  const [editingStudent, setEditingStudent] = useState(null);

  useEffect(() => {
    const fetchClasses = async () => {
      try {
        const response = await axios.get('/classes/allclasses'); // Replace with your backend endpoint for classes
        setClasses(response.data);
      } catch (err) {
        console.error('Error fetching classes:', err);
        setError('Failed to load classes. Please try again later.');
      }
    };

    fetchClasses();
  }, []);

  const handleViewClass = async (className, section) => {
    try {
      const response = await axios.get(`students/class/${className}/${section}`); // Call backend endpoint
      setStudents(response.data); // Update students state with the response
    } catch (err) {
      console.error('Error fetching class details:', err);
      setError('Failed to fetch class details. Please try again later.');
    }
  };

  const handleEditStudent = (student) => {
    setEditingStudent(student);
  };

  const handleSaveEdit = async () => {
    try {
      const response = await axios.put(`/students/updateStudent/${editingStudent.id}`, editingStudent);
      setStudents((prevStudents) =>
        prevStudents.map((student) =>
          student.id === editingStudent.id ? editingStudent : student
        )
      );
      setEditingStudent(null); // Clear editing state after saving
    } catch (err) {
      console.error('Error updating student:', err);
      setError('Failed to update student. Please try again later.');
    }
  };

  const handleDeleteStudent = async (studentId) => {
    try {
      await axios.delete(`/students/deleteStudent/${studentId}`);
      setStudents((prevStudents) => prevStudents.filter((student) => student.id !== studentId));
    } catch (err) {
      console.error('Error deleting student:', err);
      setError('Failed to delete student. Please try again later.');
    }
  };

  const handleAddStudent = async () => {
    try {
      const response = await axios.post('/students/addStudent', newStudent);
      setStudents((prevStudents) => [...prevStudents, response.data]);
      setNewStudent({
        rollNumber: '',
        name: '',
        age: '',
        grade: '',
        studentClassName: ''
      });
    } catch (err) {
      console.error('Error adding student:', err);
      setError('Failed to add student. Please try again later.');
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h1>Class List</h1>
      <table border="1" style={{ margin: '0 auto', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Roll Number</th>
            <th>Name</th>
            <th>Age</th>
            <th>Grade</th>
            <th>Class</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.id}>
              <td>{student.id}</td>
              <td>
                {editingStudent && editingStudent.id === student.id ? (
                  <input
                    type="text"
                    value={editingStudent.rollNumber}
                    onChange={(e) => setEditingStudent({ ...editingStudent, rollNumber: e.target.value })}
                  />
                ) : (
                  student.rollNumber
                )}
              </td>
              <td>
                {editingStudent && editingStudent.id === student.id ? (
                  <input
                    type="text"
                    value={editingStudent.name}
                    onChange={(e) => setEditingStudent({ ...editingStudent, name: e.target.value })}
                  />
                ) : (
                  student.name
                )}
              </td>
              <td>
                {editingStudent && editingStudent.id === student.id ? (
                  <input
                    type="number"
                    value={editingStudent.age}
                    onChange={(e) => setEditingStudent({ ...editingStudent, age: e.target.value })}
                  />
                ) : (
                  student.age
                )}
              </td>
              <td>
                {editingStudent && editingStudent.id === student.id ? (
                  <input
                    type="text"
                    value={editingStudent.grade}
                    onChange={(e) => setEditingStudent({ ...editingStudent, grade: e.target.value })}
                  />
                ) : (
                  student.grade
                )}
              </td>
              <td>
                {editingStudent && editingStudent.id === student.id ? (
                  <input
                    type="text"
                    value={editingStudent.studentClassName}
                    onChange={(e) => setEditingStudent({ ...editingStudent, studentClassName: e.target.value })}
                  />
                ) : (
                  student.studentClassName
                )}
              </td>
              <td>
                {editingStudent && editingStudent.id === student.id ? (
                  <button onClick={handleSaveEdit}>Save</button>
                ) : (
                  <>
                    <button onClick={() => handleEditStudent(student)}>Edit</button>
                    <button onClick={() => handleDeleteStudent(student.id)}>Delete</button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {error && <div style={{ color: 'red', marginTop: '20px' }}>{error}</div>}

      <div style={{ marginTop: '30px' }}>
        <h2>Add Student</h2>
        <input
          type="text"
          placeholder="Roll Number"
          value={newStudent.rollNumber}
          onChange={(e) => setNewStudent({ ...newStudent, rollNumber: e.target.value })}
        />
        <input
          type="text"
          placeholder="Name"
          value={newStudent.name}
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })}
        />
        <input
          type="number"
          placeholder="Age"
          value={newStudent.age}
          onChange={(e) => setNewStudent({ ...newStudent, age: e.target.value })}
        />
        <input
          type="text"
          placeholder="Grade"
          value={newStudent.grade}
          onChange={(e) => setNewStudent({ ...newStudent, grade: e.target.value })}
        />
        <input
          type="text"
          placeholder="Class"
          value={newStudent.studentClassName}
          onChange={(e) => setNewStudent({ ...newStudent, studentClassName: e.target.value })}
        />
        <button onClick={handleAddStudent}>Add Student</button>
      </div>
    </div>
  );
};

export default Classes;
