import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Teachers.css"; // Add styles for teachers' page

const Teachers = () => {
  const [teachers, setTeachers] = useState([]);
  const [error, setError] = useState(null);
  const [className, setClassName] = useState("");
  const [section, setSection] = useState("");

  // Fetch teachers list on component mount
  useEffect(() => {
    const fetchTeachers = async () => {
      try {
        const response = await axios.get("http://localhost:8080/teachers/allteachers");
        setTeachers(response.data);
      } catch (err) {
        setError("Failed to load teachers. Please try again later.");
        console.error("Error fetching teachers:", err);
      }
    };

    fetchTeachers();
  }, []);

  // View teachers of a specific class
  const handleViewTeachers = async (className, section) => {
    try {
      const response = await axios.get(`http://localhost:8080/teachers/class/${className}/${section}`);
      setTeachers(response.data);

      if (response.data.length === 0) {
        setError("No teachers found for this class.");
      } else {
        setError(null);
      }
    } catch (err) {
      setError("Failed to fetch teachers for this class. Please try again.");
      console.error("Error fetching teachers:", err);
    }
  };

  // Add a new teacher
  const handleAddTeacher = async (e) => {
    e.preventDefault();
    try {
      const newTeacher = {
        name: e.target.name.value,
        className: e.target.className.value,
        section: e.target.section.value,
      };

      await axios.post("http://localhost:8080/teachers/add", newTeacher);

      // Refresh the teachers list
      setTeachers((prev) => [...prev, newTeacher]);
      e.target.reset();
      setError(null);
    } catch (err) {
      setError("Failed to add teacher. Please try again.");
      console.error("Error adding teacher:", err);
    }
  };

  return (
    <div className="teachers-container">
      <h1>Teachers List</h1>

      {/* Display List of Teachers */}
      <table className="teachers-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Class</th>
            <th>Section</th>
          </tr>
        </thead>
        <tbody>
          {teachers.map((teacher) => (
            <tr key={teacher.id}>
              <td>{teacher.id}</td>
              <td>{teacher.name}</td>
              <td>{teacher.className}</td>
              <td>{teacher.section}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Error Message */}
      {error && <p className="error">{error}</p>}

      {/* View Teachers by Class & Section */}
      <div className="view-teachers-form">
        <h2>View Teachers</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleViewTeachers(className, section);
          }}
        >
          <input
            type="text"
            placeholder="Class Name"
            value={className}
            onChange={(e) => setClassName(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Section"
            value={section}
            onChange={(e) => setSection(e.target.value)}
            required
          />
          <button type="submit">View Teachers</button>
        </form>
      </div>

      {/* Add Teacher Form */}
      <div className="add-teacher-form">
        <h2>Add Teacher</h2>
        <form onSubmit={handleAddTeacher}>
          <input type="text" name="name" placeholder="Teacher Name" required />
          <input type="text" name="className" placeholder="Class Name" required />
          <input type="text" name="section" placeholder="Section" required />
          <button type="submit">Add Teacher</button>
        </form>
      </div>
    </div>
  );
};

export default Teachers;
