import React from 'react';
import Slider from 'react-slick';
import './LandingPage.css'; // Import the CSS file
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

const LandingPage = () => {
  const sliderSettings = {
    dots: true, // Add navigation dots
    infinite: true, // Loop through the slides
    speed: 500, // Transition speed
    slidesToShow: 1, // Number of slides visible
    slidesToScroll: 1, // Number of slides to scroll
    autoplay: true, // Automatically transition slides
    autoplaySpeed: 3000, // Duration between transitions
  };

  const images = [
    require('../../assets/images/image1.jpg'),
    require('../../assets/images/image2.jpg'),
    require('../../assets/images/image3.jpg'),
  ];

  return (
    <div className="landing-container">
      {/* Header Section */}
      <header className="header">
        <h1>Welcome to Panchantantra!</h1>
        <h4>Your Gateway to Knowledge</h4>
        <div className="landing-buttons">
          <button onClick={() => (window.location.href = '/classes')}>Classes</button>
          <button onClick={() => (window.location.href = '/teachers')}>Teachers</button>
        </div>
      </header>

      {/* Body Section with Image Slider */}
      <div className="body">
        <Slider {...sliderSettings}>
          {images.map((image, index) => (
            <div key={index}>
              <img src={image} alt={`Slide ${index + 1}`} className="slider-image" />
            </div>
          ))}
        </Slider>
      </div>

      {/* Footer Section */}
      <footer className="footer">
        <p>Address: Panchantantra School, 123 Education Street, Cityville</p>
      </footer>
    </div>
  );
};

export default LandingPage;
