import React, { useEffect, useState } from 'react';
import axios from '../axiosInstance';

const Classes = () => {
  const [classes, setClasses] = useState([]);
  const [selectedClassDetails, setSelectedClassDetails] = useState(null); // To store response data
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClasses = async () => {
      try {
        const response = await axios.get('/classes/allclasses'); // Replace with your backend endpoint
        setClasses(response.data);
      } catch (err) {
        console.error('Error fetching classes:', err);
        setError('Failed to load classes. Please try again later.');
      }
    };

    fetchClasses();
  }, []);

  const handleViewClass = async (className, section) => {
    try {
      const response = await axios.get(`/class/${className}/${section}`); // Call backend endpoint
      setSelectedClassDetails(response.data); // Store response data
    } catch (err) {
      console.error('Error fetching class details:', err);
      setError('Failed to fetch class details. Please try again later.');
    }
  };


  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div style={{ textAlign: 'center', marginTop: '20px' }}>
      <h1>Class List</h1>
      <table border="1" style={{ margin: '0 auto', marginTop: '20px' }}>
        <thead>
          <tr>
            <th>ID</th>
            <th>Class Name</th>
            <th>Section</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {classes.map((classItem) => (
            <tr key={classItem.id}>
              <td>{classItem.id}</td>
              <td>{classItem.name}</td>
              <td>{classItem.section}</td>
              <td>
                <button onClick={() => handleViewClass(classItem.name, classItem.section)}>View</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {selectedClassDetails && ( // Display the class details if available
        <div style={{ marginTop: '20px' }}>
          <h2>Class Details</h2>
          <p><strong>Class Name:</strong> {selectedClassDetails.name}</p>
          <p><strong>Section:</strong> {selectedClassDetails.section}</p>
          <p><strong>Other Info:</strong> {selectedClassDetails.otherInfo || 'N/A'}</p>
        </div>
      )}

      {error && <div style={{ color: 'red', marginTop: '20px' }}>{error}</div>}
    </div>
  );
};

export default Classes;
